import React, { Component } from 'react';
import { AiFillDelete } from 'react-icons/ai';
import httpGetConnection from '../../../modules/httpConnection/httpGetConnection';
import CURL from '../../../modules/url';
import './Memo.css';

export default class Memo extends Component {
    render() {
        let memo = this.props.memo;
        let _onClickMemoVideoTime = (e) => {
            e.preventDefault();
            const memoList = document.getElementById("memoList")
            if (memoList.value !== "") {
                memoList.value.seekTo(memo.memoTime);
            }
        }

        let _onDeleteClick = async (e) => {
            let url = CURL.url + 'deleteMemo';

            let _newMemoList = await httpGetConnection(url, { memoCode: Number.parseInt(memo.memoCode) },
                () => { },
                () => { });

            if (_newMemoList)
                this.props.memoListUpdate(_newMemoList);
        }

        return (
            <div className="Memo">
                <div className="MemoTitle">{memo.memoTitle} <button className="Memo-delete" onClick={_onDeleteClick}><AiFillDelete className="Memo-delete" /></button></div>
                <div className="MemoDescc">{memo.memoContent}</div>
                <a href="/content" className="MemoVideoTime" onClick={_onClickMemoVideoTime} dataset={{ value: memo.memoVideoTime }}>
                    <div value={memo.memoTime}>{`${Number.parseInt(memo.memoTime / 60)}:${memo.memoTime % 60}`} </div>
                </a>
            </div>
        )
    }
}
