import React, { Component } from 'react';
import Note from './Main/Note'
import './Main.css';
import store from '../store';
import httpGetConnection from '../modules/httpConnection/httpGetConnection';

export default class Main extends Component {
  mount = false;
  constructor(props) {
    super(props);
    this.state = store.getState();

    store.subscribe(() => {
      if (this.mount) {
        console.log(store.getState().categorySelected);
        this.setState(store.getState());
      }
    });
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (this.state === null) return false;
    if (nextState.categorySelected === this.state.categorySelected) {
      return false;
    }
    this.mount = true;
    console.log('hi', this.state.categorySelected);
    let _categoryCode = this.state.categorySelected;
    if (_categoryCode !== false) {
      let url = "http://110.11.49.218:8080/myapp/getCategoryItems";
      httpGetConnection(url, { userEmail: 'vrimd1017@gmail.com', categoryCode: _categoryCode },
        (responseJson) => {
          store.dispatch({
            type: "NOTES_LOADED",
            noteArr: responseJson
          })
        },
        () => { alert("Connection Fail") }
      )
      console.log(this.state);
    }
    return true;
  }


  componentDidMount() {
    this.mount = true;
  }

  componentDidUpdate() {
    this.mount = true;
  }

  render() {
    let notes = [];
    notes = this.state.noteArr.map(
      note => (<Note key={note.noteId} Note={note}></Note>)
    );

    return (
      <div className="Main">
        {store.getState().categorySelected ?
          notes : <p>카테고리 선택해라옹</p>
        }
      </div>
    )
  }
}
