import React, { Component } from 'react';
import Note from './Main/Note'
import './Main.css';
import store from '../store';
import httpGetConnection from '../modules/httpConnection/httpGetConnection';

export default class Main extends Component {
  mount = false;
  constructor(props) {
    super(props);
    this.state = {
      title:"",
      desc: "",
      url:"",
    }
    
    store.subscribe(() => {
      if (this.mount) {
        this.setState(store.getState());
      }
    });
  }

  shouldComponentUpdate(nextProps, nextState) {

    return true;
  }


  async componentDidMount() {
    this.mount = true;

    let _categoryCode = store.getState().categorySelected;
    if (_categoryCode !== false) {
      let url = "http://110.11.49.218:8080/myapp/getCategoryItems";
      //let url = "http://localhost:9000/test2";
      httpGetConnection(url, { userEmail: 'vrimd1017@gmail.com', categoryCode: _categoryCode },
        (responseJson) => {
          store.dispatch({
            type: "NOTES_LOADED",
            noteArr: responseJson
          })
        },
        () => { alert("Connection Fail") }
      )
    }
  }

  render() {
    let _onClickNoteCreateButton = (e) => {
        console.log(document.querySelectorAll('Note-create'));
    }

    let notes = [];
    notes = this.props.noteArr.map(
      note => (<Note key={note.noteCode} Note={note}></Note>)
    );
    return (
      <div className="Main">
        <div className="Note-craete-outer Note-craete"></div>
        <div className="Note-create-in Note-create">
            <h1>Create NOTE</h1>
            <input placeholder="Note title"/>
            <input placeholder="Note Description"/>
            <input placeholder="Video url"/>
            <div className="Note-create-buttons">
              <button className="Note-create-button">CANCLE</button>
              <button className="Note-create-button">CREATE</button>
            </div>
        </div>
        <div className="Note-tool">
          <label className="button-layout">
            노트 생성
              <button className="tool-button" onClick={_onClickNoteCreateButton}>+</button>
          </label>
          <label className="button-layout">
            노트 삭제
          <button className="tool-button">-</button>
          </label>
        </div>
        {store.getState().categorySelected ?
          notes : <p>카테고리 선택해라옹</p>
        }
      </div>
    )
  }
}
