import React, { Component } from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Nav from './components/Nav';
import WholeNavModal from './components/WholeNavModal';
import './App.css';
import store from './store';




class App extends Component {
  mount = false;
  constructor(props) {
    super(props);
    this.state = {
      onExtendNavMod: store.getState().onExtendNavMod
    }
    store.subscribe(function () {
      if(this.mount) {
      this.setState({
          onExtendNavMod: store.getState().onExtendNavMod
        })
      }
    }.bind(this));
  }

  componentDidMount() {
    this.mount = true;
  }

  render() {
    return (
      <div className="App">
        <Header></Header>
        <Nav categoryArr={store.getState().categoryArr}></Nav>
        <Main noteArr={this.state.noteArr}></Main>
        {this.state.onExtendNavMod ? <WholeNavModal /> : null}
      </div>
    )

  }
}

export default App;
