import React, { Component } from 'react';
import './Note.css';

class NoteDetail extends Component {
  render() {
    let onClickMemoAdd = (e) => {
        debugger;
    }
    return (
      <div className="NoteDetail">
        <script src = 'https://www.youtube.com/iframe_api'></script>
        <div className="YoutubeVideo">
          <iframe id="player" type="text/html" height="280px" class="YoutubeVideoI"
            src="http://www.youtube.com/embed/M7lc1UVf-VE?enablejsapi=1&origin=http://example.com"
            frameborder="0"></iframe>
        </div>
        <div className="MemoList">
          <div className="Memo">
            <div className="MemoTitle">memo1</div>
            <div className="MemoDescc">memoContent1</div>
            <div className="MemoVideoTime">03:21</div>
          </div>
          <button onClick={onClickMemoAdd}>메모추가</button>
        </div>
      </div>
    )
  };
}
export default class Note extends Component {
  state = {
    onClickNoteExtend: false
  }

  render() {
    let onClickNoteExtendButton = (e) => {
      if (e.target.innerHTML === "▼") {
        e.target.offsetParent.lastChild.style.flexDirection="column";
        e.target.innerHTML = "▲";
        e.target.offsetParent.style.height = "450px";
        this.setState({ onClickNoteExtend: true });
      } else {
        e.target.offsetParent.lastChild.style.flexDirection="row";
        e.target.innerHTML = "▼";
        e.target.offsetParent.style.height = "150px";
        this.setState({ onClickNoteExtend: false });
      }
    }
    return (
      <div className="Note">
        <div className="Note-header">
          <div className="Note-header-title">{this.props.Note.noteTitle} </div>
          <button id="Note-extend-button-id1" className="Note-button" onClick={onClickNoteExtendButton}>▼</button>
        </div>
        <div className="Note-content">
          {this.state.onClickNoteExtend ? <NoteDetail></NoteDetail> : <div className="Note-content-thumbnail">thumbnail</div>}
          <div className="Note-content-desc">
            <div className="Note-content-desc-info">{this.props.Note.noteContent}</div>
            <div className="Note-content-time">
              <div>{this.props.Note.noteVideoName} {this.props.Note.noteVideoTime} </div>
              <div className="Note-content-time-updated">{this.props.Note.noteUpdateDate}</div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
