import React, { Component } from 'react';
import Youtube from 'react-youtube';
import './NoteDetail.css';

class Memo extends Component {
    render() {
        let _onClickMemoVideoTime = (e) => {
            e.preventDefault();
            debugger;
        }

        return(
            <div className="Memo">
              <div className="MemoTitle">memo1</div>
              <div className="MemoDescc">memoContent1</div>
              <a href="/content" className="MemoVideoTime" onClick={_onClickMemoVideoTime}>
                  <div>03:21</div>
            </a>
            </div>
        )
    }
}

export default class NoteDetail extends Component {
    render() {
      let youtubeVideoPlayer = null;
      let _onReady = (e) => {
        youtubeVideoPlayer = e.target;
      }
  
      let onClickMemoAdd = (e) => {
        alert(youtubeVideoPlayer.getCurrentTime());
     }
    
      return (
        <div className="NoteDetail">
          <div className="YoutubeVideo">
            <Youtube className="YoutubeVideoI" videoId="WR-4E2r5rh4" onReady={_onReady} onPause={(e)=>{alert(e.target.getCurrentTime())}}/>
            <div className="curentMemo">
                <div className="currentMemoTitle">
                    MemoTitle
                </div>
                <div>
                    MemoContent
                </div>
            </div>
          </div>
          <div className="MemoList">
            <Memo/>
            <button onClick={onClickMemoAdd}>메모추가</button>
          </div>
        </div>
      )
    };
  }
