import React, {Component} from 'react';
import store from '../store';
import Category from './Nav/Category';
import './Nav.css';


export default class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      onExtendNavMod: false, 
      categoryArr: [], 
      categorySelected: false, 
      noteArr: []
    }
    
    store.subscribe(()=>{
      let state = store.getState();
      console.log(state);
      this.setState(store.getState());
    });
  }

  shouldComponentUpdate(nextProps, nextState) {
      if(this.state === nextState) return false;
      return true;
  }

  async componentDidMount() {
    let url =  "http://110.11.49.218:8080/myapp/getusercategorylist?userEmail=vrimd1017@gmail.com";
    let req = new XMLHttpRequest();
    req.open('GET', url, true);
    req.setRequestHeader("Authorization", "Bearer");
    req.onreadystatechange = (e) => {
      if(req.readyState === 4) {
          console.log("Status: ", req.status);
          store.dispatch({type:'CATEGORYS_LOAD', 
            categoryArr: JSON.parse(req.responseText)
          });
      }
    };
    req.send(null);
  }
  
    render() {
      let _onExtendNavMod =this.state.onExtendNavMod;
      let _categoryArr = this.state.categoryArr;
      let _categoryList = [];

      if(_categoryArr !== undefined) {
         _categoryArr.forEach(element => {
            _categoryList.push(<Category key={element.id} category={element}/>);
         });
      }
      let onClickNavCategoryTab = e => {
          store.dispatch({type:'EXTEND_NAV', onExtendNavMod:  !_onExtendNavMod});
      }

      return(
        <div className="Nav">
          <div className="Nav-home">â–¶</div>
          <div className="Nav-category-tab" onClick={onClickNavCategoryTab}>category</div>
          <ul className="Nav-category-items">
              {_categoryList}
          </ul>
          <p className="Nav-category-foot">foot</p>
        </div>
      )
    }
  }
