import {createStore} from 'redux';

//createstore 안의 function은 reducer
export default createStore(function(state, action) {
    if(state === undefined) {
        return  {
            onExtendNavMod: false,
            categoryArr: [],
            noteArr  : [],
            categorySelected: false
        }        
    }

    switch(action.type) {
        case 'CATEGORYS_LOAD':
            return Object.assign({},{categoryArr:action.categoryArr});
        case 'NOTES_LOAD':
            return Object.assign({},{noteArr:action.noteArr});
        case 'NOTES_LOADED':
            return Object.assign({},{noteArr:action.noteArr});
        case 'EXTEND_NAV':
            return Object.assign({},{onExtendNavMod:action.onExtendNavMod});  
        case 'SELECTED_CATEGORY':
            return Object.assign({},{categorySelected:action.categorySelected});
        default:
            break;
    }
    return state;
},window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
