import React, {Component} from 'react';
import store from '../store';
import Category from './Nav/Category';
import './Nav.css';
import httpGetConnection from '../modules/httpConnection/httpGetConnection';

export default class Nav extends Component {
  mount = false;
  constructor(props) {
    super(props);
    store.subscribe(()=>{
          this.setState(store.getState());
    });
  }

  // shouldComponentUpdate(nextProps, nextState) {
  //     return true;
  // }

  componentDidMount() {
      this.mount = true;
  }
  
    render() { 

      let _onExtendNavMod =store.getState().onExtendNavMod;
      let _categoryArr = this.props.categoryArr;
      let _categoryList = [];

      if(_categoryArr !== undefined) {
         _categoryArr.forEach(element => {
            _categoryList.push(<Category key={element.id} category={element}/>);
         });
      }
      let onClickNavCategoryTab = e => {
          store.dispatch({type:'EXTEND_NAV', onExtendNavMod:  !_onExtendNavMod});
      }

      return(
        <div className="Nav">
          <div className="Nav-home">â–¶</div>
          <div className="Nav-category-tab" onClick={onClickNavCategoryTab}>category</div>
          <ul className="Nav-category-items">
              {_categoryList}
          </ul>
          <p className="Nav-category-foot">foot</p>
        </div>
      )
    }
  }
