import React, { Component } from 'react';
import './Login.css';

export default class extends Component {
    state = {
        useremail: "",
        password:"",
    }
    render() {
        let _onChangeEmail = (e) => {
            this.setState({useremail: e.target.value});
        }

        let _onChangePassword = (e) => {
            this.setState({password: e.target.value});
        }

        let _onSubmitClick = (e) => {
            debugger;
            e.preventDefault();
            this.setState({password: e.target.value});
        }
        return (
            <div className="Login" onSubmit={_onSubmitClick}>
                <form className="LoginForm" src="/content" method="POST">
                    <h1>LOGIN</h1>
                    <div className="textbox">
                    <label>
                        Email:
                    <input className="email input" type="text" value={this.state.useremail} 
                        onChange={_onChangeEmail} placeholder="useremail@example.com" />
                    </label>
                    </div>
                    <div className="textbox">
                    <label>
                        Password:
                    <input className="password input" type="password" value={this.state.password} 
                        onChange={_onChangePassword} />
                    </label>
                    </div>
                    <div styel={{display:"flex"}}>
                        <input className="sign input" tpye="button" value="SIGNUP"/>
                        <input className="submit input" type="submit" value="GO"/>
                    </div>
                </form>
                <form className="SignForm" src="/content">
                <h1>LOGIN</h1>
                    <div className="textbox">
                    <label>
                        Email:
                    <input className="email input" type="text" value={this.state.useremail} 
                        onChange={_onChangeEmail} placeholder="useremail@example.com" />
                    </label>
                    </div>
                    <div className="textbox">
                    <label>
                        Password:
                    <input className="password input" type="password" value={this.state.password} 
                        onChange={_onChangePassword} />
                    </label>
                    </div>
                    <div styel={{display:"flex"}}>
                        <input className="sign input" tpye="button" value="SIGNUP"/>
                        <input className="submit input" type="submit" value="GO"/>
                    </div>
                </form>
            </div>
        )
    }
}
