import React, {Component} from 'react';
import './Category.css';

export default class Category extends Component {
    render() {
      let category = null;
      let subCategoryList = [];
      let mainCategory = this.props.category;

      let onClickCategoryExtend = (e) => {
        let temp = mainCategory.map(x=>x).filter((x)=>x.id===Number(e.target.id));
        debugger;
        document.getElementById('Category-sub-1').style.display = 'flex';
        document.getElementById('Category-main-button-1').style.display = 'none';
        }
  
      let onClickCategoryShort = (e) => {
        document.getElementById('Category-sub-1').style.display = 'none';
        document.getElementById('Category-main-button-1').style.display = 'block';
      }

      
      if(mainCategory!==undefined) {
      
       category = 
       <div className="Category">
          <div id="" className="Category-main">
            <button id={mainCategory.id} className="Category-button" onClick={onClickCategoryExtend}>▼</button>
            <div className="Category-content">{mainCategory.categoryName}</div>
          </div>
          {mainCategory.subCategoryArr.forEach(element => {
           subCategoryList.push(<div id={"Category-sub-"+element.id} className="Category-sub">
              <button id={mainCategory.id} className="Category-button" onClick={onClickCategoryShort}>▲</button>
              <div className="Category-content">{element.categoryName}</div>
            </div>)
          })}
          {subCategoryList}
      </div>
      }
  
      return(
        <li className="Nav-category-item">
          {category}
        </li>
      )
    }
  }
