import {createStore} from 'redux';

//createstore 안의 function은 reducer
export default createStore(function(state, action) {
    if(state === undefined) {
        return  {
            onExtendNavMod: false,
            categoryArr: [
                {id:1, categoryName: 'category1', subCategoryArr:[
                        {id:'1-1', categoryName: 'category1-1'},{id:'1-2', categoryName: 'category1-2'},{id:'1-3', categoryName: 'category1-3'}
                    ]
                },
                {id:2, categoryName: 'category2', subCategoryArr:[
                         {id:'2-1', categoryName: 'category2-1'},{id:'2-2', categoryName: 'category2-2'},{id:'2-3', categoryName: 'category2-3'}
                    ]
                },
                {id:3, categoryName: 'category3', subCategoryArr:[
                        {id:'3-1', categoryName: 'category3-1'},{id:'3-2', categoryName: 'category3-2'}
                    ]
                }
            ],
            categorySelected: false
        }        
    }

    switch(action.type) {
        case 'EXTEND_NAV':
            return {...state, onExtendNavMod:action.onExtendNavMod};
            
        case 'SELECTED_CATEGORY':
            return {categorySelected:action.categorySelected};
        
        case 'SELECTED_CURRENT_VIDEO':
            return {player: (action.assign({}, action)).player};
        default:
            break;
    }
    return state;
},window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
