import React, {Component} from 'react';
import Header from './components/Header'
import Main from './components/Main'
import Nav from './components/Nav'
import './App.css';
import store from './store';
import Category from './components/Nav/Category';


class WholeNavModal extends Component {
  state = store.getState();
  render() {
    let _categoryArr = this.state.categoryArr;
    let _categoryList = [];
    let divCategoryList = [];
    if(_categoryArr!==undefined){
       _categoryArr.forEach(mainCategorys => {
        let subCategoryInThis = [];
       _categoryList.push(<div>{mainCategorys.categoryName}</div>);
         mainCategorys.subCategoryArr.forEach(subCategorys=>{
           subCategoryInThis.push(<div>{subCategorys.categoryName}</div>);
         });
       _categoryList.push(subCategoryInThis);
      });


      _categoryList.foreEach(category=>{
        divCategoryList.push(<div class="category">{category}</div>); 
      });
    }

    console.log('Modal');
    let _onExtendNavMod = store.getState().onExtendNavMod;
    let onClickNavCategoryTab = e => {
      store.dispatch({type:'EXTEND_NAV', onExtendNavMod:  !_onExtendNavMod});
    }

    return(
      <div className="WholeNavModal">
          <button onClick={onClickNavCategoryTab}>x</button>
          {_categoryList}
      </div>
    )
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
       onExtendNavMod: store.getState().onExtendNavMod
    }
    store.subscribe(function(){
      this.setState({
        onExtendNavMod: store.getState().onExtendNavMod
      })
    }.bind(this));
  }

  render() {
    return(
      <div className="App">
        <Header></Header>
        <Nav></Nav>
        <Main></Main>
        {this.state.onExtendNavMod ? <WholeNavModal/> : null}
      </div>
    )

  }
}

export default App;
