import React, {Component} from 'react';
import Header from './components/Header'
import Main from './components/Main'
import Nav from './components/Nav'
import './App.css';
import store from './store';


class WholeNavModal extends Component {
  render() {

    let onClickNavCategoryTab = e => {
      store.dispatch({type:'EXTEND_NAV', onExtendNavMod:  !_onExtendNavMod});
    }
    return(
      <div className="WholeNavModal">
          <button onClick={onClickNavCategoryTab}>x</button>
      </div>
    )
  }
}

class App extends Component {
  state = {onExtendNavMod: store.getState().onExtendNavMod}
  constructor(props) {
    super(props);
    store.subscribe(function(){
      console.log('hello');
      this.setState({
        onExtendNavMod: store.getState().onExtendNavMod
      })
    }.bind(this));
  }

  render() {
    return(
      <div className="App">
        <Header></Header>
        <Nav></Nav>
        <Main></Main>
        {this.state.onExtendNavMod ? <WholeNavModal/> : null}
      </div>
    )

  }
}

export default App;
