import React, { Component } from 'react';
import store from '../../store';
import './Category.css';

export default class Category extends Component {
  render() {
    let category = null;
    let subCategoryList = [];
    let mainCategory = this.props.category;

    let onClickCategoryExtend = (e) => {
      if (e.target.innerText === "▼") {
        e.target.innerText = "▲";
        mainCategory.subCategoryArr.forEach(element => {
          document.getElementById('Category-sub-' + element.id).style.display = 'flex';
        });
        document.getElementById('Category-sub-' + mainCategory.id).style.display = 'flex';
        document.getElementById('Category-main-button' + mainCategory.id).style.display = 'none';
      } else {
        e.target.innerText = "▼";
        mainCategory.subCategoryArr.forEach(element => {
          document.getElementById('Category-sub-' + element.id).style.display = 'none';
        });
        document.getElementById('Category-sub-' + mainCategory.id).style.display = 'none';
        document.getElementById('Category-main-button' + mainCategory.id).style.display = 'block';
      }

    }

    let onClickCategoryShort = (e) => {

    }

    let onClickCategoryContent = (e) => {
      store.dispatch({ type: 'SELECTED_CATEGORY', categorySelected: e.target.getAttribute('value') });
    }

    if (mainCategory !== undefined) {

      category =
        <div className="Category">
          <div id="" className="Category-main" onClick={onClickCategoryExtend}>
            <button id={"Category-main-button" + mainCategory.id} className="Category-button" >▼</button>
            <div className="Category-content" onClick={onClickCategoryContent} value={mainCategory.categoryName}>{mainCategory.categoryName}</div>
          </div>
          {mainCategory.subCategoryArr.forEach(element => {
            subCategoryList.push(<div id={"Category-sub-" + element.id} className="Category-sub">
              <div className="Category-content" onClick={onClickCategoryContent} value={element.categoryName}>{element.categoryName}</div>
            </div>)
          })}
          {subCategoryList}
        </div>
    }

    return (
      <li className="Nav-category-item">
        {category}
      </li>
    )
  }
}
