import React, { Component } from 'react';
import Header from './components/Header';
import Main from './components/Main';
import Nav from './components/Nav';
import WholeNavModal from './components/WholeNavModal';
import './App.css';
import store from './store';

class App extends Component {
  mount = false;
  constructor(props) {
    super(props);
    this.state = {
      onExtendNavMod: false,
      categoryArr: [],
      noteArr  : [],
      categorySelected: false
    }

    store.subscribe(function () {
      if(this.mount===true) {
        this.setState(store.getState())
      }
    }.bind(this));
  }

  shouldComponentUpdate(nextProps, nextState) {
    if(this.state.categorySelected !== nextState.categorySelected) {
      alert(this.categorySelected);
      if(this.categorySelected===undefined) return true;

      store.dispatch({type:'CATEGORY_SELECTED', categorySelected:false});
      return true;
    } else {
      return false;
    }
  } 

  componentDidMount() {
      this.mount =true;
  }
  render() {
    return (
      <div className="App">
        <Header></Header>
        <Nav categoryArr={this.state.categoryArr}></Nav>
        {this.state.categorySelected?<Main noteArr={this.state.noteArr}></Main>:null}
        {this.state.onExtendNavMod? <WholeNavModal categoryArr={this.state.categoryArr} /> : null}
      </div>
    )

  }
}

export default App;
