import React, {Component} from 'react';
import Note from './Main/Note'
import './Main.css';
import store from '../store';

export default class Main extends Component {
    constructor(props) {
      super(props);
      this.state = {
        categorySelected : store.getState().categorySelected,
        noteArr  : [
          {noteId: "1", noteTitle: "The Note1", noteContent: "The Content1", noteVideoName:"동영상 이름1", noteVideoTime: "20분", noteUpdateDate: "수정된 시간", noteThumbnail: "srcaddress"},
          {noteId: "2", noteTitle: "The Note2", noteContent: "The Content2", noteVideoName:"동영상 이름2", noteVideoTime: "20분", noteUpdateDate: "수정된 시간", noteThumbnail: "srcaddress"},
          {noteId: "3", noteTitle: "The Note3", noteContent: "The Content3", noteVideoName:"동영상 이름3", noteVideoTime: "20분", noteUpdateDate: "수정된 시간", noteThumbnail: "srcaddress"},
          {noteId: "4", noteTitle: "The Note4", noteContent: "The Content4", noteVideoName:"동영상 이름4", noteVideoTime: "20분", noteUpdateDate: "수정된 시간", noteThumbnail: "srcaddress"},
        ]
      }

      store.subscribe(()=>{
        this.setState({
          categorySelected : store.getState().categorySelected,
          noteArr : [
              {}
          ]
        });
      });
    }

    shouldComponentUpdate(nextProps, nextState) {
      if(this.state === null) return false;
      if(nextState.categorySelected === this.state.categorySelected) {
        return false;
      }
      return true;
    }
    render() {
      let notes = [];
      notes = this.state.noteArr.map(
        note => <Note></Note>
      );
      debugger;
      return(
        <div className="Main">
           {store.getState().categorySelected ?
           {notes} : <p>카테고리 선택해라옹</p> 
          }
           </div>
      )
    }
  }
