import {createStore} from 'redux';

//createstore 안의 function은 reducer
export default createStore(function(state, action) {
    if(state === undefined) {
        return  {
            onExtendNavMod: false,
            categoryArr: [],
            categorySelected: false
        }        
    }

    switch(action.type) {
        case 'CATEGORYS_LOAD':
            return {...state, categoryArr:action.categoryArr};
            case 'NOTES_LOAD':
                return {...state, categoryArr:action.noteArr};
        case 'EXTEND_NAV':
            return {...state, onExtendNavMod:action.onExtendNavMod};  
        case 'SELECTED_CATEGORY':
            let _state = {...state, categorySelected:action.categorySelected};
            console.log(_state); 
            return _state;
        default:
            break;
    }
    return state;
},window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
