import React, {Component} from 'react';
import Header from './components/Header'
import Main from './components/Main'
import Nav from './components/Nav'
import './App.css';
import store from './store';

class App extends Component {
  state = {onExtendNavMod: store.getState().onExtendNavMod}
  constructor(props) {
    super(props);
    store.subscribe(function(){
      console.log('hello');
      this.setState({
        onExtendNavMod: store.getState().onExtendNavMod
      })
    }.bind(this));
  }
  render() {
    if(this.state.onExtendNavMod === true) {
      alert('fuck');
    } else{
      alert('what');
    }
    return(
      <div className="App">
        <Header></Header>
        <Nav></Nav>
        <Main></Main>
      </div>
    )

  }
}

export default App;
