import React, { Component } from 'react';
import Youtube from 'react-youtube';
import './NoteDetail.css';
import store from '../../../store';

class Memo extends Component {
    render() {
        let _onClickMemoVideoTime = (e) => {
            e.preventDefault();
            if(store.getState().player !== undefined)
                alert(store.getState().player.getCurrentTime());
            debugger;
        }

        return(
            <div className="Memo">
              <div className="MemoTitle">memo1</div>
              <div className="MemoDescc">memoContent1</div>
              <a href="/content" className="MemoVideoTime" onClick={_onClickMemoVideoTime} value={191}>
                  <div >{`${Number.parseInt(191/60)}:${191%60}`}</div>
            </a>
            </div>
        )
    }
}

export default class NoteDetail extends Component {
    render() {
      let youtubeVideoPlayer = null;
      let  _onReady = (e) => {
        youtubeVideoPlayer = e.target; 
        store.dispatch({type:"SELECTED_CURRENT_VIDEO", player:youtubeVideoPlayer});
      }
  
      let onClickMemoAdd = (e) => {
        if(youtubeVideoPlayer !== null)
            alert(youtubeVideoPlayer.getCurrentTime());
     }
    
      return (

        <div className="NoteDetail">
          <div className="YoutubeVideo">
            <Youtube className="YoutubeVideoI" videoId="WR-4E2r5rh4" onReady={_onReady}/>
            <div className="curentMemo">
                <div className="currentMemoTitle">
                    MemoTitle
                </div>
                <div>
                    MemoContent
                </div>
            </div>
          </div>
          <div className="MemoList">
              <Memo></Memo>
            <button onClick={onClickMemoAdd}>메모추가</button>
          </div>
        </div>
      )
    };
  }
