import React, { Component } from 'react';
import './Memo.css';

export default class Memo extends Component {
    render() {
        let memo = this.props.memo;
        let _onClickMemoVideoTime = (e) => {
            e.preventDefault();
            const memoList = document.getElementById("memoList")
            if(memoList.value !== "") {
                memoList.value.seekTo(memo.memoVideoTime);
                this.props.onStateChange(this.props.memo.index);
            }
                
        }

        return(
            <div className="Memo">
              <div className="MemoTitle">{memo.memoTitle}</div>
              <div className="MemoDescc">{memo.memoContent}</div>
              <a href="/content" className="MemoVideoTime" onClick={_onClickMemoVideoTime} dataset={{value: memo.memoVideoTime}}>
                  <div value={memo.memoVideoTime}>{`${Number.parseInt(memo.memoVideoTime/60)}:${memo.memoVideoTime%60}`} </div>
            </a>
            </div>
        )
    }
}
