import {createStore} from 'redux';

//createstore 안의 function은 reducer
export default createStore(function(state, action) {
    if(state === undefined) {
        return  {
            onExtendNavMod: false,
            categoryArr: [
                {id:1, categoryTitle: 'category1', subCategoryArr:[
                        {id:'1-1', categoryTitle: 'category1-1'},{id:'1-2', categoryTitle: 'category1-2'},{id:'1-3', categoryTitle: 'category1-3'}
                    ]
                },
                {id:2, categoryTitle: 'category2', subCategoryArr:[
                         {id:'2-1', categoryTitle: 'category2-1'},{id:'2-2', categoryTitle: 'category2-2'},{id:'2-3', categoryTitle: 'category2-3'}
                    ]
                },
                {id:3, categoryTitle: 'category3', subCategoryArr:[
                        {id:'3-1', categoryTitle: 'category3-1'},{id:'3-2', categoryTitle: 'category3-2'}
                    ]
                }
            ],
            categorySelected: false
        }        
    }

    switch(action.type) {
        case 'CATEGORYS_LOAD':
            return {...state, noteArr:action.noteArr};
        case 'EXTEND_NAV':
            return {...state, onExtendNavMod:action.onExtendNavMod};
            
        case 'SELECTED_CATEGORY':
            return {categorySelected:action.categorySelected};
        default:
            break;
    }
    return state;
},window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())
