import React, { Component } from 'react';
import Note from './Main/Note'
import './Main.css';
import store from '../store';
import httpGetConnection from '../modules/httpConnection/httpGetConnection';

export default class Main extends Component {
  mount = false;
  constructor(props) {
    super(props);
    this.state = store.getState();

    store.subscribe(() => {
      if (this.mount) {
        let _state = store.getState();
        this.setState(_state);
      }
    });
  }

  shouldComponentUpdate(nextProps, nextState) {
    if (this.state === null) return false;
    if (this.state.categorySelected === nextState.categorySelected) return false;
    console.log('hi', this.state.categorySelected);
    let _categoryCode = this.state.categorySelected;
    console.log("zzzzzzzzzz",this.state);
    if (_categoryCode !== false) {
      //let url = "http://110.11.49.218:8080/myapp/getCategoryItems";
      //{ userEmail: 'vrimd1017@gmail.com', categoryCode: _categoryCode }
      let url = "http://localhost:9000/test2";
      let json = httpGetConnection(url, null,
        (responseJson) => {
          store.dispatch({
            type: "NOTES_LOADED",
            noteArr: responseJson
          })
        },
        () => { alert("Connection Fail") }
      )
    }
    return true;
  }


 async componentDidMount() {
    this.mount = true;
  }

  render() {
    let notes = [];
    notes = this.state.noteArr.map(
      note => (<Note key={note.noteId} Note={note}></Note>)
    );

    return (
      <div className="Main">
        {store.getState().categorySelected ?
          notes : <p>카테고리 선택해라옹</p>
        }
      </div>
    )
  }
}
