import React, { Component } from 'react';
import Memo from './Memo';
import Youtube from 'react-youtube';
import './NoteDetail.css';



export default class NoteDetail extends Component {
    state = {
        memoList: [
            {memoId:"1",memoTitle: "memo1", memoContent: "memoContent1", memoVideoTime: 191},
            {memoId:"2",memoTitle: "memo2", memoContent: "memoContent2", memoVideoTime: 291},
            {memoId:"3",memoTitle: "memo3", memoContent: "memoContent3", memoVideoTime: 391},
            {memoId:"4",memoTitle: "memo4", memoContent: "memoContent4", memoVideoTime: 491}
        ],
        title: "This will be Title section",
        content: "This will be Content section"
    }
    render() {
    let memoIndex = -1;

    let memosWithIndex = this.state.memoList.map(memo=>Object.assign(memo,{index: ++memoIndex}))
      let youtubeVideoPlayer = null;
      let  _onReady = (e) => {
        youtubeVideoPlayer = e.target;
        const memoList = document.getElementById("memoList");
        memoList.value = youtubeVideoPlayer;
      }
  
      let onClickMemoAdd = (e) => {
        if(youtubeVideoPlayer !== null)
            alert(youtubeVideoPlayer.getCurrentTime());
     }

    let _onChangeTitle = (e) => {
        this.setState({
            title : e.target.value
        });
    }
    let _onChageContent = (e) => {
        this.setState({
            content: e.target.value
        });
    }

    let _onChangeVideoTime = (index) => {
        this.setState({
            title: memosWithIndex[index].memoTitle,
            content: memosWithIndex[index].memoContent
        });
        memoIndex = index;
    }

    let _onChange = (e) => {
        setTimeout(()=>{
            console.log(Number(e.target.getCurrentTime()))
        }, 1);
        memoIndex++;
    }

    let memoArray = memosWithIndex.map(memo=><Memo onChange={_onChangeVideoTime} key={memo.memoId} memo={memo}/>);
      return (

        <div className="NoteDetail">
          <div className="YoutubeVideo">
            <Youtube className="YoutubeVideoI" videoId="WR-4E2r5rh4" onReady={_onReady} onChange={_onChange}/>
            <div className="curentMemo">
                <div className="currentMemoTitle">
                    <input onChange={_onChangeTitle} value={this.state.title}></input>
                </div>
                <div className="memoContentDiv">
                    <input onChange={_onChageContent} value={this.state.content}></input>
                    <button className="memo-add-button" onClick={onClickMemoAdd}>메모추가</button>
                </div>
            </div>
          </div>
          <div id="memoList" className="MemoList" value="">
              {memoArray}
          </div>
        </div>
      )
    };
  }
