import React, { Component } from 'react';
import store from '../store';
import './WholeNavModal.css';

export default class WholeNavModal extends Component {
  mount = false;
  constructor(props) {
    super(props);

    store.subscribe(() => {
      if (this.mount) {
        this.setState(store.getState());
      }
    }).bind(this);
  };

  componentDidMount() {
    this.mount = true;
    const WholeNavModal = document.getElementById("WholeNavModelId");
    for(let i=0; i<WholeNavModal.childElementCount; i++)
      WholeNavModal.children.item(i).style.opacity="0";
    WholeNavModal.style.display = "flex";
    debugger;
    setTimeout(() => {
      let pixel = 0;
      let clear = setInterval(() => {
        if (pixel === 300) {
          for(let i=0; i<WholeNavModal.childElementCount; i++)
              WholeNavModal.children.item(i).style.opacity="100";
          clearInterval(clear); 
        }
        pixel+=3;
        WholeNavModal.style.width = `${pixel}px`;
        WholeNavModal.style.height = `${pixel}px`;
      }, 30);
    }, 20);
  }

  render() {

    let _categoryArr = this.props.categoryArr;
    console.log(_categoryArr);
    let _categoryParent = [];

    if (_categoryArr !== undefined) {
      _categoryArr.forEach(main => {
        let subCategoryList = [];
        main.categoryArr.forEach(sub => {
          subCategoryList.push(<div className="whole-category-sub">{sub.categoryName}</div>)
        });
        _categoryParent.push(
          <div className="whole-category-parent">
            <div className="whole-category-main">{main.categoryName}</div>
            {subCategoryList}
          </div>
        )
      });
    }

    console.log('Modal');
    let onClickNavCategoryTab = e => {
      store.dispatch({ type: 'EXTEND_NAV', onExtendNavMod: false });
    }

    return (
      <div id="WholeNavModelId" className="WholeNavModal">
        <button className="WholeNavModal-exit-button" onClick={onClickNavCategoryTab}>x</button>
        {_categoryParent}
      </div>
    )
  }
}
