import React, { Component } from 'react';
import store from '../store';
import './WholeNavModal.css';

export default class WholeNavModal extends Component {
    state = store.getState();
    render() {
      let _categoryArr = this.state.categoryArr;
      let _categoryParent = [];
  
      if (_categoryArr !== undefined) {
  
        _categoryArr.forEach(main => {
          let subCategoryList = [];
          main.subCategoryArr.forEach(sub=> { 
          subCategoryList.push(<div className="whole-category-sub">{sub.categoryName}</div>)
          });
          _categoryParent.push(
            <div className="whole-category-parent">
              <div className="whole-category-main">{main.categoryName}</div>
                {subCategoryList}
            </div>
          )
        });
      }
  
      console.log('Modal');
      let _onExtendNavMod = store.getState().onExtendNavMod;
      let onClickNavCategoryTab = e => {
        store.dispatch({ type: 'EXTEND_NAV', onExtendNavMod: !_onExtendNavMod });
      }
  
      return (
        <div className="WholeNavModal">
          <button className="WholeNavModal-exit-button" onClick={onClickNavCategoryTab}>x</button>
          {_categoryParent}
        </div>
      )
    }
  }
